import moment from 'moment'
import Shrines from '../../constants/apis'; // api

export default {
  components: {},
  data() {
    return {
      selectedMonth: null, //変更可の日付（月単位でのみ使用）
      selectedDate: null,
      dateLabel: "",
      dates: [],
      spaces: [],
      eventList: [],
      selectedStyle: {
        color: 'black',
        'font-weight': 700,
      },
      eventStyle: {
        color: '#EE827C'
      },
      selectedEventStyle: {
        'background-color': '#EE827C',
        color: 'white',
        'border-radius': '50%',
      }
    }
  },
  created() {
    Shrines.event(this.$route.params.id)
    .then(response => {
      this.eventList = response.data.data;
      for(let k of Object.keys(this.eventList)) {
        this.eventList[k].event_date = this.eventList[k].event_date.replace(/\//g, "");
      }
      this.selectedDate = moment().format('YYYYMMDD');
      this.selectedMonth = moment();
    })
  },
  methods: {
    setMonth: function(num) {
      if(num === 0) {
        this.selectedMonth = moment(this.selectedMonth).subtract(1, 'months');
      } else {
        this.selectedMonth = moment(this.selectedMonth).add(1, 'months');
      }
    },
    setDate: function(event) {
      this.selectedDate = event.currentTarget.id
    }
  },
  watch: {
    selectedMonth: function() {
      this.dateLabel = moment(this.selectedMonth).format('YYYY年MM月');
      this.spaces = [];
      this.dates = [];
      for(let i = 0; i < moment(this.selectedMonth).startOf('month').day(); i++) {
        this.spaces[i] = i;
      }
      for(let i = 0; i < moment(this.selectedMonth).daysInMonth(); i++) {
        this.dates[i] = {
          date: moment(this.selectedMonth).startOf('month').add(i, 'day').format('YYYYMMDD'),
          dateNumber: i + 1,
        }
        let eventNumber = 0;
        for(let k of Object.keys(this.eventList)) {
          if(this.eventList[k].event_date === this.dates[i].date) {
            eventNumber++;
          }
        }
        if(eventNumber === 0) {
          eventNumber = "-";
        }
        this.dates[i].eventNumber = eventNumber;
      }
    }
  },
};
